mapper.select:
/*아이디 중복검사: SQL ID: NonSocialMemberSQL.countMemberByLoginId*/

	SELECT
		COUNT(*)
	FROM
		nonsocial_member
	WHERE login_id = #{login_id}

/*아이디 중복검사: SQL ID: NonSocialMemberSQL.countMemberByName*/

	SELECT
		COUNT(*)
	FROM
		nonsocial_member
	WHERE user_name = #{user_name}

/*이름에 따른 회원찾기 : NonSocialMemberSQL.getOneMemberByName */
SELECT auth_id, login_id, email, password, user_name, authority
FROM nonsocial_member
WHERE login_id = #{login_id}
